package com.rankhep.woosongimport android.content.Intentimport android.os.Bundleimport android.support.v7.app.AppCompatActivityimport android.widget.Toastimport com.rankhep.woosong.model.CheckSendModelimport com.rankhep.woosong.model.LostItemimport com.rankhep.woosong.utils.NetworkHelperimport kotlinx.android.synthetic.main.activity_check_certification_num.*import okhttp3.MediaTypeimport okhttp3.RequestBodyimport retrofit2.Callimport retrofit2.Callbackimport retrofit2.Responseclass CheckCertificationNumActivity : AppCompatActivity() {    lateinit var item: LostItem    lateinit var phone: String    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_check_certification_num)        item = intent.getSerializableExtra("item") as LostItem        phone = intent.getStringExtra("phone")        acceptBtn.setOnClickListener {            if (!certificationEditText.text.matches(Regex("\\d\\d\\d\\d\\d\\d"))) {                Toast.makeText(applicationContext, "6글자를 입력해주세요.", Toast.LENGTH_SHORT).show()            } else {                val intent = Intent(this@CheckCertificationNumActivity, FindItemMessageActivity::class.java)                intent.putExtra("item", item)                val param = CheckSendModel.toJson(CheckSendModel(phone, certificationEditText.text.toString()))                val body = RequestBody.create(MediaType.parse("application/json"), param.toString())                NetworkHelper.networkInstance.checkCertification(body).enqueue(object : Callback<Any> {                    override fun onFailure(call: Call<Any>, t: Throwable) {                        t.printStackTrace()                    }                    override fun onResponse(call: Call<Any>, response: Response<Any>) {                        if (response.isSuccessful) {                            intent.putExtra("phone", phone)                            startActivity(intent)                            finish()                        }else{                            Toast.makeText(applicationContext, "에러" + response.message(),Toast.LENGTH_SHORT).show()                        }                    }                })            }        }    }}