package com.rankhep.woosongimport android.support.v7.app.AppCompatActivityimport android.os.Bundleimport android.widget.Toastimport com.rankhep.woosong.utils.NetworkHelperimport kotlinx.android.synthetic.main.activity_check_certification_num.*class CheckCertificationNumActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_check_certification_num)        acceptBtn.setOnClickListener {            if(certificationEditText.text.matches(Regex("\\d\\d\\d\\d\\d\\d"))){                Toast.makeText(applicationContext,"6글자를 입력해주세요.", Toast.LENGTH_SHORT).show()            }else{               NetworkHelper.networkInstance                //TODO : 서버연동            }        }    }}