package com.rankhep.woosongimport android.content.Intentimport android.os.Bundleimport android.support.v7.app.AppCompatActivityimport com.rankhep.woosong.model.LostItemimport kotlinx.android.synthetic.main.activity_find_notice.*class FindNoticeActivity : AppCompatActivity() {    lateinit var item: LostItem    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_find_notice)        item = intent.getSerializableExtra("item") as LostItem        acceptBtn.setOnClickListener {            val intent = Intent(this@FindNoticeActivity, FindPhoneNumberActivity::class.java)            intent.putExtra("item", item)            startActivity(intent)            finish()        }    }}