package com.rankhep.woosongimport android.content.Intentimport android.support.v7.app.AppCompatActivityimport android.os.Bundleimport android.widget.Toastimport com.rankhep.woosong.model.LostItemimport com.rankhep.woosong.utils.NetworkHelperimport kotlinx.android.synthetic.main.activity_find_phone_number.*class FindPhoneNumberActivity : AppCompatActivity() {    private lateinit var item: LostItem    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_find_phone_number)        item = intent.getSerializableExtra("item") as LostItem        nextBtn.setOnClickListener {            if(phoneNumberEditText.text.toString().matches(Regex("\\d\\d\\d-\\d\\d\\d\\d-\\d\\d\\d\\d"))){                val intent = Intent(this@FindPhoneNumberActivity, CheckCertificationNumActivity::class.java)                intent.putExtra("item", item)                NetworkHelper.networkInstance            }else{                Toast.makeText(applicationContext,"전화번호를 입력해주세요 \bex)010-0000-0000",Toast.LENGTH_SHORT).show()            }        }    }}