package com.rankhep.woosongimport android.content.Intentimport android.support.v7.app.AppCompatActivityimport android.os.Bundleimport com.rankhep.woosong.model.LostItemimport com.squareup.picasso.Picassoimport kotlinx.android.synthetic.main.activity_lost_item_info.*class LostItemInfoActivity : AppCompatActivity() {    lateinit var item: LostItem    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_lost_item_info)        item = intent.getSerializableExtra("item") as LostItem        Picasso.get().load("http://54.180.159.3/${item.imagePath}").into(image)        image.clipToOutline = true        nameText.text = item.itemname        locationText.text = item.getLocation        timeText.text = item.daytime        featureText.text = item.description        findBtn.setOnClickListener {            val intent = Intent(this@LostItemInfoActivity, FindNoticeActivity::class.java)            intent.putExtra("item",item)            startActivityForResult(intent, 333)        }        backBtn.setOnClickListener {            finish()        }    }    override fun onActivityResult(requestCode: Int, resultCode: Int, data: Intent?) {        super.onActivityResult(requestCode, resultCode, data)    }}