package com.rankhep.woosongimport android.content.Intentimport android.os.Bundleimport android.support.v7.app.AppCompatActivityimport android.support.v7.widget.LinearLayoutManagerimport android.view.Viewimport com.rankhep.woosong.adapter.MainItemAdapterimport com.rankhep.woosong.model.LostItemimport com.rankhep.woosong.model.LostItemListResponseModelimport com.rankhep.woosong.utils.NetworkHelperimport kotlinx.android.synthetic.main.activity_main.*import retrofit2.Callimport retrofit2.Callbackimport retrofit2.Responseclass MainActivity : AppCompatActivity(), MainItemAdapter.OnItemClickedListener {    override fun itemClickListener(v: View, position: Int, item: LostItem) {        val intent = Intent(this@MainActivity, LostItemInfoActivity::class.java)        intent.putExtra("item", item)        startActivity(intent)    }    val mAdapter = MainItemAdapter()    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_main)        val layoutManager = LinearLayoutManager(this, LinearLayoutManager.HORIZONTAL, false)        mAdapter.setOnItemListener(this)        NetworkHelper.networkInstance.getList().enqueue(object : Callback<LostItemListResponseModel> {            override fun onFailure(call: Call<LostItemListResponseModel>, t: Throwable) {                t.printStackTrace()            }            override fun onResponse(                call: Call<LostItemListResponseModel>,                response: Response<LostItemListResponseModel>            ) {                if (response.isSuccessful) {                    response.body()?.let {                        mAdapter.setItem(it.list)                    }                }            }        })        itemList.run {            this.layoutManager = layoutManager            adapter = mAdapter        }        registerBtn.setOnClickListener {            val intent = Intent(applicationContext, QRCodeActivity::class.java)            startActivity(intent)        }        searchBtn.setOnClickListener {            if (searchEditText.text.toString() != "") {                val intent = Intent(this@MainActivity, SearchActivity::class.java)                intent.putExtra("search", searchEditText.text.toString())                startActivity(intent)            }        }        alreadyFindBtn.setOnClickListener {        }    }}