package com.rankhep.woosong.adapterimport android.support.v7.widget.RecyclerViewimport android.util.Logimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport com.rankhep.woosong.Rimport com.rankhep.woosong.model.LostItemimport com.squareup.picasso.Picassoimport kotlinx.android.synthetic.main.item_missing_item.view.*class MainItemAdapter : RecyclerView.Adapter<MainItemAdapter.ViewHolder>() {    private var items = ArrayList<LostItem>()    interface OnItemClickedListener {        fun itemClickListener(v: View, position: Int, item: LostItem)    }    private var listener: OnItemClickedListener? = null    fun setOnItemListener(listener: OnItemClickedListener) {        this.listener = listener        notifyDataSetChanged()    }    fun addItem(item: LostItem) {        items.add(item)        notifyDataSetChanged()    }    fun setItem(items: List<LostItem>) {        this.items.clear()        this.items.addAll(items)        notifyDataSetChanged()    }    override fun onCreateViewHolder(p0: ViewGroup, p1: Int): ViewHolder =        ViewHolder(LayoutInflater.from(p0.context).inflate(R.layout.item_missing_item, null, false))    override fun getItemCount(): Int = items.size    override fun onBindViewHolder(viewHolder: ViewHolder, position: Int) {        viewHolder.itemView.run {            findLocationText.text = items[position].getLocation            itemName.text = items[position].itemname            findDateText.text = items[position].daytime            setOnClickListener {                listener?.itemClickListener(it, position, items[position])            }            Picasso.get().load("http://54.180.159.3/${items[position].imagePath}").into(imgView)        }    }    class ViewHolder(v: View) : RecyclerView.ViewHolder(v) {    }}