package com.rankhep.woosong.adapterimport android.support.v7.widget.RecyclerViewimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport com.rankhep.woosong.Rimport com.rankhep.woosong.model.LostItemimport kotlinx.android.synthetic.main.item_missing_item.view.*class MainItemAdapter : RecyclerView.Adapter<MainItemAdapter.ViewHolder>() {    private var items = ArrayList<LostItem>()    fun addItem(item:LostItem) {        items.add(item)        notifyDataSetChanged()    }    fun setItem(items: ArrayList<LostItem>) {        this.items = items        notifyDataSetChanged()    }    override fun onCreateViewHolder(p0: ViewGroup, p1: Int): ViewHolder =            ViewHolder(LayoutInflater.from(p0.context).inflate(R.layout.item_missing_item, null, false))    override fun getItemCount(): Int = items.size    override fun onBindViewHolder(viewHolder: ViewHolder, p1: Int) {        viewHolder.itemView.run {            findLocationText.text = "남영"            itemName.text = "장지갑"            findDateText.text = "06/23"        }    }    class ViewHolder(v: View) : RecyclerView.ViewHolder(v) {    }}