package com.rankhep.woosong.modelimport java.io.Serializableclass LostItem(    var no: Int = 0,    var itemname: String = "",    var daytime: String ="",    var getLocation: String="",    var storageLocation: String="",    var imagePath: String="",    var user: String="",    var owner: String="",    var description:String="",    var reservDay:String="") : Serializable {    override fun equals(other: Any?): Boolean {        if (this === other) return true        if (javaClass != other?.javaClass) return false        other as LostItem        if (no != other.no) return false        if (itemname != other.itemname) return false        if (daytime != other.daytime) return false        if (getLocation != other.getLocation) return false        if (storageLocation != other.storageLocation) return false        if (imagePath != other.imagePath) return false        if (user != other.user) return false        if (owner != other.owner) return false        return true    }    override fun hashCode(): Int {        var result = no        result = 31 * result + itemname.hashCode()        result = 31 * result + daytime.hashCode()        result = 31 * result + getLocation.hashCode()        result = 31 * result + storageLocation.hashCode()        result = 31 * result + imagePath.hashCode()        result = 31 * result + user.hashCode()        result = 31 * result + owner.hashCode()        return result    }    override fun toString(): String {        return "LostItem(no=$no, itemname='$itemname', daytime='$daytime', getLocation='$getLocation', storageLocation='$storageLocation', image='$imagePath', user='$user', owner='$owner')"    }}